cmd-syntax:
{
    "command": "remote-network4-list"
    "arguments": {
        "remote": {
            <specification of the database to connect to>
        }
    }
}

resp-syntax:
{
    "result": 0,
    "text": "2 IPv4 shared network(s) found.",
    "arguments": {
        "shared-networks": [
            {
                "name": <first shared network name>,
                "metadata": {
                    "server-tag": <server tag>
                }
            },
            {
                "name": <second shared network name>,
                "metadata": {
                    "server-tag": <server tag>
                }
            }
        ],
        "count": 2
    }
}
