cmd-syntax:
{
    "command": "remote-option4-global-get",
    "arguments": {
        "options": [
            {
                "code": <option code>,
                "space": <option space>
            }
        ],
        "remote": {
            <specification of the database to connect to>
        }
}

resp-syntax:
{
    "result": 0,
    "text": "DHCPv4 option <option code> in <option space> found.",
    "arguments": {
        "options": [
            {
                <option information>,
                "metadata": {
                    "server-tag": <server tag>
                }
            }
        ]
    }
}
